import React, { useState } from 'react';
import firebase from "firebase/app";
import "firebase/auth";
import firebaseConfig from './FireBaseConfig';
import './CreateAccount.css';

if(firebase.apps.length === 0) {
    firebase.initializeApp(firebaseConfig);
}

const CreateAccount = () => {
    const [user, setUser] = useState({
        isSignUp: false,
        name: '',
        email: '',
        password: '',
        error: '',
        success: false
    })



    const handleChange = e => {
        let  isInputValid;
        if(e.target.name === 'email'){
            isInputValid = /\S+@\S+\.\S+/.test(e.target.value);

        }
        if(e.target.name === 'password'){
            isInputValid = e.target.value.length > 6;
        }
        if(isInputValid){
            const newUserInfo = {...user};
            newUserInfo[e.target.name] = e.target.value;
            setUser(newUserInfo);
        }
    }
    const handleSubmit = e => {
        if(user.email && user.password){
            firebase.auth().createUserWithEmailAndPassword(user.email, user.password)
            .then(res => {
                const newUserInfo = {...user};
                newUserInfo.error = '';
                newUserInfo.success = true;
                setUser(newUserInfo);
            })
            .catch((error) => {
                const newUserInfo = {...user};
                newUserInfo.error = error.message;
                newUserInfo.success = false;
                setUser(newUserInfo);
            });
        }
        e.preventDefault();
    }

    return (
        <div className='main'>
            <p>name: {user.name}</p>
            <p>email: {user.email}</p>
            <p>pass: {user.password}</p>
            <form onSubmit={handleSubmit}>
                <input type="text" onBlur={handleChange} name='name' placeholder='your name' /><br/><br/>
                <input onBlur={handleChange} name='email' type="text" placeholder='your email' required/><br/><br/>
                <input onBlur={handleChange}  type="password" name='password' placeholder='your password' required /><br/><br/>
                <p style={{color:'red'}}>{user.error}</p>
                {
                    user.success && <p style={{color:'green'}}>user create successfully</p>
                }
                <input onClick={() => changeToLogin(!user.isSignUp)} type="submit" value="submit"/>
            </form>
        </div>
    );
};

export default CreateAccount;
